svg.script:
  //
  function init() {
    for (let x=0; x<100; x+=10) {
      let u = document.createElementNS("http://www.w3.org/2000/svg", "use");
      u.setAttribute("href", "#r");
      u.setAttribute("x", x);
      u.setAttribute("y", 90-x);
      document.rootElement.append(u);
    }
  } 

  document.rootElement.addEventListener('load', init);
//
